mapper.update:
update cas.apply
serialno=#{serialno, jdbcType=VARCHAR}, customerno=#{customerno, jdbcType=VARCHAR}, businesscode=#{businesscode, jdbcType=VARCHAR}, content=#{content, jdbcType=CLOB}, status=#{status, jdbcType=VARCHAR}, remark=#{remark, jdbcType=VARCHAR}, insertdate=#{insertdate, jdbcType=VARCHAR}, inserttime=#{inserttime, jdbcType=VARCHAR}, updatedate=#{updatedate, jdbcType=VARCHAR}, updatetime=#{updatetime, jdbcType=VARCHAR},
