mapper.insert:

	insert into cas.aus_authone(
		customerno,password,failcount,registersource,insertdate,
		inserttime,updatedate,updatetime,source,onlinestatus,
		loginsource
	) values(
		#{customerno, jdbcType=VARCHAR},#{password, jdbcType=VARCHAR},#{failcount, jdbcType=INTEGER},#{registersource, jdbcType=VARCHAR},#{insertdate, jdbcType=VARCHAR},
		#{inserttime, jdbcType=VARCHAR},#{updatedate, jdbcType=VARCHAR},#{updatetime, jdbcType=VARCHAR},#{source, jdbcType=VARCHAR},#{onlinestatus, jdbcType=VARCHAR},
		#{loginsource, jdbcType=VARCHAR}
)

mapper.update:
update cas.aus_authone
customerno=#{customerno, jdbcType=VARCHAR}, password=#{password, jdbcType=VARCHAR}, failcount=#{failcount, jdbcType=INTEGER}, registersource=#{registersource, jdbcType=VARCHAR}, insertdate=#{insertdate, jdbcType=VARCHAR}, inserttime=#{inserttime, jdbcType=VARCHAR}, updatedate=#{updatedate, jdbcType=VARCHAR}, updatetime=#{updatetime, jdbcType=VARCHAR}, source=#{source, jdbcType=VARCHAR}, onlinestatus=#{onlinestatus, jdbcType=VARCHAR}, loginsource=#{loginsource, jdbcType=VARCHAR},
where customerno = #{customerno}

update.foreach:
