customerno=#{item.customerno, jdbcType=VARCHAR}, password=#{item.password, jdbcType=VARCHAR}, failcount=#{item.failcount, jdbcType=INTEGER}, registersource=#{item.registersource, jdbcType=VARCHAR}, insertdate=#{item.insertdate, jdbcType=VARCHAR}, inserttime=#{item.inserttime, jdbcType=VARCHAR}, updatedate=#{item.updatedate, jdbcType=VARCHAR}, updatetime=#{item.updatetime, jdbcType=VARCHAR}, source=#{item.source, jdbcType=VARCHAR}, onlinestatus=#{item.onlinestatus, jdbcType=VARCHAR}, loginsource=#{item.loginsource, jdbcType=VARCHAR},
