mapper.select:

	select
		branchid "branchid",
		branchname "branchname",
		parentid "parentid",
		branchtype "branchtype",
		province "province",
		city "city",
		county "county",
		stertreet "stertreet",
		showorder "showorder",
		curnumber "curnumber"
	from cas.branch


	 and branchid=#{branchid}
	 and branchname=#{branchname}
	 and parentid=#{parentid}
	 and branchtype=#{branchtype}
	 and province=#{province}
	 and city=#{city}
	 and county=#{county}
	 and stertreet=#{stertreet}
	 and showorder=#{showorder}
	 and curnumber=#{curnumber}

