select
	id "id",
	membertype "membertype",
	registersource "registersource",
	certificatetype "certificatetype",
	certificateno "certificateno",
	mobile "mobile",
	cardno "cardno",
	bank "bank",
	companyname "companyname",
	status "status",
	insertdate "insertdate",
	updatedate "updatedate",
	corname "corname",
	corcardtype "corcardtype",
	corcardno "corcardno",
	membername "membername"
from cas.cas_register_info


 and id=#{id}
 and membertype=#{membertype}
 and registersource=#{registersource}
 and certificatetype=#{certificatetype}
 and certificateno=#{certificateno}
 and mobile=#{mobile}
 and cardno=#{cardno}
 and bank=#{bank}
 and companyname=#{companyname}
 and status=#{status}
 and insertdate=#{insertdate}
 and updatedate=#{updatedate}
 and corname=#{corname}
 and corcardtype=#{corcardtype}
 and corcardno=#{corcardno}
 and membername=#{membername}

mapper.update:
update cas.cas_register_info
id=#{id, jdbcType=VARCHAR}, membertype=#{membertype, jdbcType=VARCHAR}, registersource=#{registersource, jdbcType=VARCHAR}, certificatetype=#{certificatetype, jdbcType=VARCHAR}, certificateno=#{certificateno, jdbcType=VARCHAR}, mobile=#{mobile, jdbcType=VARCHAR}, cardno=#{cardno, jdbcType=VARCHAR}, bank=#{bank, jdbcType=VARCHAR}, companyname=#{companyname, jdbcType=VARCHAR}, status=#{status, jdbcType=VARCHAR}, insertdate=#{insertdate, jdbcType=VARCHAR}, updatedate=#{updatedate, jdbcType=VARCHAR}, corname=#{corname, jdbcType=VARCHAR}, corcardtype=#{corcardtype, jdbcType=VARCHAR}, corcardno=#{corcardno, jdbcType=VARCHAR}, membername=#{membername, jdbcType=VARCHAR},
where id = #{id}

update.foreach:
update cas.cas_register_info
id=#{item.id, jdbcType=VARCHAR}, membertype=#{item.membertype, jdbcType=VARCHAR}, registersource=#{item.registersource, jdbcType=VARCHAR}, certificatetype=#{item.certificatetype, jdbcType=VARCHAR}, certificateno=#{item.certificateno, jdbcType=VARCHAR}, mobile=#{item.mobile, jdbcType=VARCHAR}, cardno=#{item.cardno, jdbcType=VARCHAR}, bank=#{item.bank, jdbcType=VARCHAR}, companyname=#{item.companyname, jdbcType=VARCHAR}, status=#{item.status, jdbcType=VARCHAR}, insertdate=#{item.insertdate, jdbcType=VARCHAR}, updatedate=#{item.updatedate, jdbcType=VARCHAR}, corname=#{item.corname, jdbcType=VARCHAR}, corcardtype=#{item.corcardtype, jdbcType=VARCHAR}, corcardno=#{item.corcardno, jdbcType=VARCHAR}, membername=#{item.membername, jdbcType=VARCHAR},
where id = #{item.id}

