mapper.update:
update cas.cust
customerno=#{customerno, jdbcType=VARCHAR}, loginname=#{loginname, jdbcType=VARCHAR}, registersource=#{registersource, jdbcType=VARCHAR}, membertype=#{membertype, jdbcType=VARCHAR}, certificatetype=#{certificatetype, jdbcType=VARCHAR}, certificateno=#{certificateno, jdbcType=VARCHAR}, linkmanname=#{linkmanname, jdbcType=VARCHAR}, mobile=#{mobile, jdbcType=VARCHAR}, cardno=#{cardno, jdbcType=VARCHAR}, bank=#{bank, jdbcType=VARCHAR}, companyname=#{companyname, jdbcType=VARCHAR}, email=#{email, jdbcType=VARCHAR}, status=#{status, jdbcType=VARCHAR}, insertdate=#{insertdate, jdbcType=VARCHAR}, inserttime=#{inserttime, jdbcType=VARCHAR}, updatedate=#{updatedate, jdbcType=VARCHAR}, updatetime=#{updatetime, jdbcType=VARCHAR}, issave=#{issave, jdbcType=VARCHAR}, source=#{source, jdbcType=VARCHAR}, corname=#{corname, jdbcType=VARCHAR}, corcardtype=#{corcardtype, jdbcType=VARCHAR}, corcardno=#{corcardno, jdbcType=VARCHAR}, cormob=#{cormob, jdbcType=VARCHAR},
where customerno = #{customerno}

