mapper.select:

	select
		customerno "customerno",
		employeename "employeename",
		loginname "loginname",
		branchid "branchid",
		certificatetype "certificatetype",
		certificateno "certificateno",
		mobile "mobile",
		email "email",
		status "status",
		province "province",
		city "city",
		county "county",
		insertdate "insertdate",
		inserttime "inserttime",
		updatedate "updatedate",
		updatetime "updatetime"
	from cas.employee


	 and customerno=#{customerno}
	 and employeename=#{employeename}
	 and loginname=#{loginname}
	 and branchid=#{branchid}
	 and certificatetype=#{certificatetype}
	 and certificateno=#{certificateno}
	 and mobile=#{mobile}
	 and email=#{email}
	 and status=#{status}
	 and province=#{province}
	 and city=#{city}
	 and county=#{county}
	 and insertdate=#{insertdate}
	 and inserttime=#{inserttime}
	 and updatedate=#{updatedate}
	 and updatetime=#{updatetime}

for update


select
	customerno "customerno",
	employeename "employeename",
	loginname "loginname",
	branchid "branchid",
	certificatetype "certificatetype",
	certificateno "certificateno",
	mobile "mobile",
	email "email",
	status "status",
	province "province",
	city "city",
	county "county",
	insertdate "insertdate",
	inserttime "inserttime",
	updatedate "updatedate",
	updatetime "updatetime"
from cas.employee


 and customerno=#{customerno}
 and employeename=#{employeename}
 and loginname=#{loginname}
 and branchid=#{branchid}
 and certificatetype=#{certificatetype}
 and certificateno=#{certificateno}
 and mobile=#{mobile}
 and email=#{email}
 and status=#{status}
 and province=#{province}
 and city=#{city}
 and county=#{county}
 and insertdate=#{insertdate}
 and inserttime=#{inserttime}
 and updatedate=#{updatedate}
 and updatetime=#{updatetime}


select
	customerno "customerno",
	employeename "employeename",
	loginname "loginname",
	branchid "branchid",
	certificatetype "certificatetype",
	certificateno "certificateno",
	mobile "mobile",
	email "email",
	status "status",
	province "province",
	city "city",
	county "county",
	insertdate "insertdate",
	inserttime "inserttime",
	updatedate "updatedate",
	updatetime "updatetime"
from cas.employee


 and customerno=#{customerno}
 and employeename=#{employeename}
 and loginname=#{loginname}
 and branchid=#{branchid}
 and certificatetype=#{certificatetype}
 and certificateno=#{certificateno}
 and mobile=#{mobile}
 and email=#{email}
 and status=#{status}
 and province=#{province}
 and city=#{city}
 and county=#{county}
 and insertdate=#{insertdate}
 and inserttime=#{inserttime}
 and updatedate=#{updatedate}
 and updatetime=#{updatetime}

mapper.update:
update cas.employee
customerno=#{customerno, jdbcType=VARCHAR}, employeename=#{employeename, jdbcType=VARCHAR}, loginname=#{loginname, jdbcType=VARCHAR}, branchid=#{branchid, jdbcType=VARCHAR}, certificatetype=#{certificatetype, jdbcType=VARCHAR}, certificateno=#{certificateno, jdbcType=VARCHAR}, mobile=#{mobile, jdbcType=VARCHAR}, email=#{email, jdbcType=VARCHAR}, status=#{status, jdbcType=VARCHAR}, province=#{province, jdbcType=VARCHAR}, city=#{city, jdbcType=VARCHAR}, county=#{county, jdbcType=VARCHAR}, insertdate=#{insertdate, jdbcType=VARCHAR}, inserttime=#{inserttime, jdbcType=VARCHAR}, updatedate=#{updatedate, jdbcType=VARCHAR}, updatetime=#{updatetime, jdbcType=VARCHAR},
where customerno = #{customerno}

update.foreach:
update cas.employee
customerno=#{item.customerno, jdbcType=VARCHAR}, employeename=#{item.employeename, jdbcType=VARCHAR}, loginname=#{item.loginname, jdbcType=VARCHAR}, branchid=#{item.branchid, jdbcType=VARCHAR}, certificatetype=#{item.certificatetype, jdbcType=VARCHAR}, certificateno=#{item.certificateno, jdbcType=VARCHAR}, mobile=#{item.mobile, jdbcType=VARCHAR}, email=#{item.email, jdbcType=VARCHAR}, status=#{item.status, jdbcType=VARCHAR}, province=#{item.province, jdbcType=VARCHAR}, city=#{item.city, jdbcType=VARCHAR}, county=#{item.county, jdbcType=VARCHAR}, insertdate=#{item.insertdate, jdbcType=VARCHAR}, inserttime=#{item.inserttime, jdbcType=VARCHAR}, updatedate=#{item.updatedate, jdbcType=VARCHAR}, updatetime=#{item.updatetime, jdbcType=VARCHAR},
where customerno = #{item.customerno}

