customerno=#{item.customerno, jdbcType=VARCHAR}, orgcustomerno=#{item.orgcustomerno, jdbcType=VARCHAR}, status=#{item.status, jdbcType=VARCHAR},
where orgcustomerno = #{item.orgcustomerno} and customerno = #{item.customerno}

