mapper.select:

	select
		serialno "serialno",
		customerno "customerno",
		orgcustomerno "orgcustomerno",
		certificatetype "certificatetype",
		certificateno "certificateno",
		ismember "ismember",
		invitecode "invitecode",
		status "status",
		failuretime "failuretime",
		insertdate "insertdate",
		inserttime "inserttime",
		updatedate "updatedate",
		updatetime "updatetime",
		issendsms "issendsms"
	from cas.invite_code


	 and serialno=#{serialno}
	 and customerno=#{customerno}
	 and orgcustomerno=#{orgcustomerno}
	 and certificatetype=#{certificatetype}
	 and certificateno=#{certificateno}
	 and ismember=#{ismember}
	 and invitecode=#{invitecode}
	 and status=#{status}
	 and failuretime=#{failuretime}
	 and insertdate=#{insertdate}
	 and inserttime=#{inserttime}
	 and updatedate=#{updatedate}
	 and updatetime=#{updatetime}
	 and issendsms=#{issendsms}

