 and updatetime=#{updatetime}


select
	serialno "serialno",
	customerno "customerno",
	version "version",
	username "username",
	certificatetype "certificatetype",
	certificateno "certificateno",
	moneyscale "moneyscale",
	mobile "mobile",
	dutyway "dutyway",
	moneytype "moneytype",
	amount "amount",
	paydatelimit "paydatelimit",
	address "address",
	position "position",
	sortnum "sortnum",
	insertdate "insertdate",
	inserttime "inserttime",
	updatedate "updatedate",
	updatetime "updatetime"
from cas.member_submit


 and serialno=#{serialno}
 and customerno=#{customerno}
 and version=#{version}
 and username=#{username}
 and certificatetype=#{certificatetype}
 and certificateno=#{certificateno}
 and moneyscale=#{moneyscale}
 and mobile=#{mobile}
 and dutyway=#{dutyway}
 and moneytype=#{moneytype}
 and amount=#{amount}
 and paydatelimit=#{paydatelimit}
 and address=#{address}
 and position=#{position}
 and sortnum=#{sortnum}
 and insertdate=#{insertdate}
 and inserttime=#{inserttime}
 and updatedate=#{updatedate}
 and updatetime=#{updatetime}

mapper.update:
update cas.member_submit
serialno=#{serialno, jdbcType=VARCHAR}, customerno=#{customerno, jdbcType=VARCHAR}, version=#{version, jdbcType=INTEGER}, username=#{username, jdbcType=VARCHAR}, certificatetype=#{certificatetype, jdbcType=VARCHAR}, certificateno=#{certificateno, jdbcType=VARCHAR}, moneyscale=#{moneyscale, jdbcType=VARCHAR}, mobile=#{mobile, jdbcType=VARCHAR}, dutyway=#{dutyway, jdbcType=VARCHAR}, moneytype=#{moneytype, jdbcType=VARCHAR}, amount=#{amount, jdbcType=VARCHAR}, paydatelimit=#{paydatelimit, jdbcType=VARCHAR}, address=#{address, jdbcType=VARCHAR}, position=#{position, jdbcType=VARCHAR}, sortnum=#{sortnum, jdbcType=INTEGER}, insertdate=#{insertdate, jdbcType=VARCHAR}, inserttime=#{inserttime, jdbcType=VARCHAR}, updatedate=#{updatedate, jdbcType=VARCHAR}, updatetime=#{updatetime, jdbcType=VARCHAR},
where serialno = #{serialno}

update.foreach:
update cas.member_submit
serialno=#{item.serialno, jdbcType=VARCHAR}, customerno=#{item.customerno, jdbcType=VARCHAR}, version=#{item.version, jdbcType=INTEGER}, username=#{item.username, jdbcType=VARCHAR}, certificatetype=#{item.certificatetype, jdbcType=VARCHAR}, certificateno=#{item.certificateno, jdbcType=VARCHAR}, moneyscale=#{item.moneyscale, jdbcType=VARCHAR}, mobile=#{item.mobile, jdbcType=VARCHAR}, dutyway=#{item.dutyway, jdbcType=VARCHAR}, moneytype=#{item.moneytype, jdbcType=VARCHAR}, amount=#{item.amount, jdbcType=VARCHAR}, paydatelimit=#{item.paydatelimit, jdbcType=VARCHAR}, address=#{item.address, jdbcType=VARCHAR}, position=#{item.position, jdbcType=VARCHAR}, sortnum=#{item.sortnum, jdbcType=INTEGER}, insertdate=#{item.insertdate, jdbcType=VARCHAR}, inserttime=#{item.inserttime, jdbcType=VARCHAR}, updatedate=#{item.updatedate, jdbcType=VARCHAR}, updatetime=#{item.updatetime, jdbcType=VARCHAR},
where serialno = #{item.serialno}

