mapper.select:

	select
		serialno "serialno",
		customerno "customerno",
		companyname "companyname",
		businessno "businessno",
		leader "leader",
		regicapital "regicapital",
		regicapitalbz "regicapitalbz",
		linkmanname "linkmanname",
		mobile "mobile",
		servicecustomerno "servicecustomerno",
		businessscope "businessscope",
		companyintro "companyintro",
		workaddress "workaddress",
		registeraddress "registeraddress",
		insertdate "insertdate",
		inserttime "inserttime",
		updatedate "updatedate",
		updatetime "updatetime"
	from cas.organ_rollin


	 and serialno=#{serialno}
	 and customerno=#{customerno}
	 and companyname=#{companyname}
	 and businessno=#{businessno}
	 and leader=#{leader}
	 and regicapital=#{regicapital}
	 and regicapitalbz=#{regicapitalbz}
	 and linkmanname=#{linkmanname}
	 and mobile=#{mobile}
	 and servicecustomerno=#{servicecustomerno}
	 and businessscope=#{businessscope}
	 and companyintro=#{companyintro}
	 and workaddress=#{workaddress}
	 and registeraddress=#{registeraddress}
	 and insertdate=#{insertdate}
	 and inserttime=#{inserttime}
	 and updatedate=#{updatedate}
	 and updatetime=#{updatetime}

