mapper.update:
update cas.organ_rollin
serialno=#{serialno, jdbcType=VARCHAR}, customerno=#{customerno, jdbcType=VARCHAR}, companyname=#{companyname, jdbcType=VARCHAR}, businessno=#{businessno, jdbcType=VARCHAR}, leader=#{leader, jdbcType=VARCHAR}, regicapital=#{regicapital, jdbcType=VARCHAR}, regicapitalbz=#{regicapitalbz, jdbcType=VARCHAR}, linkmanname=#{linkmanname, jdbcType=VARCHAR}, mobile=#{mobile, jdbcType=VARCHAR}, servicecustomerno=#{servicecustomerno, jdbcType=VARCHAR}, businessscope=#{businessscope, jdbcType=CLOB}, companyintro=#{companyintro, jdbcType=CLOB}, workaddress=#{workaddress, jdbcType=CLOB}, registeraddress=#{registeraddress, jdbcType=CLOB}, insertdate=#{insertdate, jdbcType=VARCHAR}, inserttime=#{inserttime, jdbcType=VARCHAR}, updatedate=#{updatedate, jdbcType=VARCHAR}, updatetime=#{updatetime, jdbcType=VARCHAR},
