		#{item.comments, jdbcType=VARCHAR},#{item.paramStatus, jdbcType=INTEGER},#{item.sortNo, jdbcType=INTEGER}
	)

mapper.update:
update cas.sys_params_config
exchange_id=#{exchangeId, jdbcType=VARCHAR}, distributor_id=#{distributorId, jdbcType=VARCHAR}, param_type=#{paramType, jdbcType=VARCHAR}, param_id=#{paramId, jdbcType=VARCHAR}, param_value=#{paramValue, jdbcType=VARCHAR}, comments=#{comments, jdbcType=VARCHAR}, param_status=#{paramStatus, jdbcType=INTEGER}, sort_no=#{sortNo, jdbcType=INTEGER},
where exchange_id = #{exchangeId} and param_type = #{paramType} and distributor_id = #{distributorId} and param_id = #{paramId}

update.foreach:
update cas.sys_params_config
