mapper.select:

		select
			customerno "customerno",
			loginname  "loginname",
			registersource "registersource",
			membertype "membertype",
			certificatetype "certificatetype",
			certificateno "certificateno",
			linkmanname "linkmanname",
			mobile "mobile",
			cardno  "cardno",
			bank    "bank",
		    companyname "companyname",
			email "email",
			status "status",
			insertdate "insertdate",
			inserttime "inserttime",
			updatedate "updatedate",
			updatetime "updatetime",
            issave     "issave",
            source     "source"
		from cas.cust


             and customerno=#{customerno}
             and loginname=#{loginname}
             and registersource=#{registersource}
             and membertype=#{membertype}
             and certificatetype=#{certificatetype}
             and certificateno=#{certificateno}
             and linkmanname=#{linkmanname}
             and mobile=#{mobile}
             and cardno=#{cardno}
             and bank=#{bank}
             and companyname=#{companyname}
             and email=#{email}
             and status=#{status}
             and insertdate=#{insertdate}
             and inserttime=#{inserttime}
             and updatedate=#{updatedate}
             and updatetime=#{updatetime}
             and issave=#{issave}
             and source=#{source}
