mapper.select:

		select
			id "id",
			registersource "registersource",
			membertype "membertype",
			certificatetype "certificatetype",
			certificateno "certificateno",
			mobile "mobile",
			cardno  "cardno",
			bank    "bank",
		    companyname "companyname",
			status "status",
			insertdate "insertdate",
			updatedate "updatedate",
            corname     "corname",
            corcardtype     "corcardtype",
            corcardno     "corcardno",
            membername     "membername"
		from cas.cas_register_info


             and id=#{id}
             and registersource=#{registersource}
             and membertype=#{membertype}
             and certificatetype=#{certificatetype}
             and certificateno=#{certificateno}
             and mobile=#{mobile}
             and cardno=#{cardno}
             and bank=#{bank}
             and companyname=#{companyname}
             and status=#{status}
             and insertdate=#{insertdate}
             and updatedate=#{updatedate}
             and corname=#{corname}
             and corcardtype=#{corcardtype}
             and corcardno=#{corcardno}
             and membername=#{membername}

mapper.update:
update cas.cas_register_info t
registersource=#{registersource}, membertype=#{membertype}, certificatetype=#{certificatetype}, certificateno=#{certificateno}, mobile=#{mobile}, cardno=#{cardno}, bank=#{bank}, companyname=#{companyname}, status=#{status}, insertdate=#{insertdate}, updatedate=#{updatedate}, corname=#{corname}, corcardtype=#{corcardtype}, corcardno=#{corcardno}, membername=#{membername},
and t.id=#{id}
